const xwchar* ReadWString( const bitstream& BS )
{
    s32 i   = 0;
    u32 ch;

    xwchar TempBuff[ MAX_STRING_LENGTH ];

    while( i < MAX_STRING_LENGTH )
    {
        if( BS.ReadFlag() )
        {
            BS.ReadRangedU32( ch, 0, 127 );
            TempBuff[ i ] = (xwchar)ch;
        }
        else
        {
            BS.ReadU16( TempBuff[ i ] );
        }

        if( TempBuff[i] == 0 ) 
        {
            break;
        }

        i++;
    }

    TempBuff[ MAX_STRING_LENGTH - 1 ] = 0;

    xwchar* pString = new xwchar[ i + 1 ];

    for( s32 k = 0; k < MAX_STRING_LENGTH; k++ )
    {
        pString[ k ] = TempBuff[ k ];

        if( pString[ k ] == 0 )
        {
            break;
        }
    }

    pString[ i ] = 0;
    
    return pString;
}

//==============================================================================

void WriteWString( bitstream& BS, const xwchar* pString )
{
    ASSERT( pString );

    s32 i   = 0;
    while(  (  i < MAX_STRING_LENGTH   ) && 
            (  pString[ i ] != 0       ) )
    {     
        if( BS.WriteFlag( pString[ i ] < 128 ) )
        {
            BS.WriteRangedU32( pString[ i ], 0, 127 );
        }
        else
        {
            BS.WriteU16( pString[ i ] );
        }

        i++;
    }

    BS.WriteFlag( TRUE );
    BS.WriteRangedU32( 0, 0, 127 );
}

