void InitMessages( void )
{
    g_StringTableMgr.LoadTable( "Msg", xfs( "%s\\%s", g_RscMgr.GetRootDirectory(), 
                                            "ENG_Msg_strings.stringbin" ) );

    if( MsgClient.m_SelfName.GetLength() == 0 )
    {
        MsgClient.m_SelfName = g_StringTableMgr( "Msg", "PLAYER_NAME_SELF" );
    }

    // Now load all the msg strings into the messages.
    for( s32 i = 0; i < MSG_LAST_PRESET; i++ )
    {
        ASSERT( MsgTable[i].m_Index == i );
        
        if( MsgTable[i].m_pStringName )
        {
            MsgTable[i].m_pMsgString[0] = MsgMgr.GetStr( xfs( "%s_A", MsgTable[i].m_pStringName ) );
            MsgTable[i].m_pMsgString[1] = MsgMgr.GetStr( xfs( "%s_B", MsgTable[i].m_pStringName ) );
            MsgTable[i].m_pMsgString[2] = MsgMgr.GetStr( xfs( "%s_C", MsgTable[i].m_pStringName ) );
        }

        if( (MsgTable[i].m_pStringName   != NULL) && 
            (MsgTable[i].m_pMsgString[0] == NULL) )
        {
            x_DebugMsg( "%s not found!\n", MsgTable[i].m_pStringName );    
        }
        
        ASSERT( MsgTable[i].m_pMsgString[0] != s_NullString );
    }
}                                                                                                                                                                                   
                                                                                                                                                                                    
